package ch02.ex04;

public class C02_Over_fow_Calc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte b = 126;
		System.out.println(b);
		b++;
		System.out.println(b);
		b++;
		System.out.println(b);
		b++;
		System.out.println(b);
		
		//b = 128; // compile error
		//bo, byte's value is -128 ~ 127

	}

}
