package ch03.ex10;

public class C01_Assign {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 3;
		System.out.println(i += 2);
		System.out.println(i -= 2);
		System.out.println(i *= 2);
		System.out.println(i /= 2);
		System.out.println(i %= 2);
		

	}

}
